      income=toNumber(replaceAll(data[i+2],"subTotal=",""))/100;
      if(income>30){

         type="ชำระผ่านเครดิต";        
         if(not contains(credit_min,"♂️")) {

            if(contains(credit_min,"❌")){

               WF[0]="👛「เครดิต」แบนงานเครดิต";
