               sqrt();}

            if(not contains(credit_max,"❌") and income>credit_max){

               WF[0]="👛「เครดิต」สูงกว่ากำหนด";
               WF[1]="({credit_max}) ≠ {income}";
               sqrt();
            }
         }
      }
      else {type="เงินสด";income=toNumber(replaceAll(data[i+7],"subTotal=",""))/100;break}
   } 
}



//————————>  DESCRIPTION  <————————————————————————————————————————————————————————————————————————*/


